import { IObjRef } from "soui4/IObjRef";
import { IStringW } from "soui4/IString";
import { IXmlNode } from "soui4/IXml";

export class IObject extends IObjRef{
    IsClass(name:string):boolean;
    GetObjectClass():string;
    GetObjectType():number;

    GetID():number;
    SetID(id:number):void;
    GetName():string;
    SetName(name:string):void;

    InitFromXml(xml:IXmlNode):boolean;
    SetAttribute(name:string,value:string,loading:boolean):number;
    GetAttribute(name:string,value:IStringW):boolean;    
}